import { Component } from '@angular/core';

@Component({
  selector: 'app-mortgage-calculator',
  templateUrl: './mortgage-calculator.component.html',
  styleUrls: ['./mortgage-calculator.component.css']
})
export class MortgageCalculatorComponent {
  purchasePrice!: number;
  downPayment!: number;
  repaymentTime!: number;
  interestRate!: number;

  get loanAmount(): number {
    return this.purchasePrice - this.downPayment;
  }

  get monthlyPayment(): number {
    const monthlyInterestRate = this.interestRate / 100 / 12;
    const numberOfPayments = this.repaymentTime * 12;
    return (
      (this.loanAmount * monthlyInterestRate) /
      (1 - Math.pow(1 + monthlyInterestRate, -numberOfPayments))
    );
  }
}
<div class="container mt-5 border rounded-sm p-3 bg-light">
    <h2 class="text-primary">Mortgage Calculator</h2>
    <div class="mb-3">
      <label class="form-label">Purchase Price:</label>
      <input class="form-control" type="number" [(ngModel)]="purchasePrice" />
    </div>
    <div class="mb-3">
      <label class="form-label">Down Payment:</label>
      <input class="form-control" type="number" [(ngModel)]="downPayment" />
    </div>
    <div class="mb-3">
      <label class="form-label">Repayment Time (years):</label>
      <input class="form-control" type="number" [(ngModel)]="repaymentTime" />
    </div>
    <div class="mb-3">
      <label class="form-label">Interest Rate (%):</label>
      <input class="form-control" type="number" [(ngModel)]="interestRate" />
    </div>
    <div>
      <h3 class="text-success">Loan Amount: {{ loanAmount }}</h3>
      <h3 class="text-danger">Monthly Payment: {{ monthlyPayment | number: '1.2-2' }}</h3>
    </div>
</div>
